import useInput from "../hooks/use-input";
const BasicForm = (props) => {

  const {
    value: enteredFirstName,
    isValid: enteredFirstNameIsValid,
    hasError: firstNameInputHasError,
    valueChangeHandler: firstNameChangedHandler,
    valueBlurHandler: firstNameBlurHandler,
    reset: resetFirstNameInput
  } = useInput(value => value.trim !== '');

  const {
    value: enteredLastName,
    isValid: enteredLastNameIsValid,
    hasError: lastNameInputHasError,
    valueChangeHandler: lastNameChangedHandler,
    valueBlurHandler: lastNameBlurHandler,
    reset: resetLastNameInput
  } = useInput(value => value.trim !== '');

  const {
    value: enteredEmail,
    isValid: enteredEmailIsValid,
    hasError: emailInputHasError,
    valueChangeHandler: emailChangedHandler,
    valueBlurHandler: emailBlurHandler,
    reset: resetEmailInput
  } = useInput(value => value.includes('@'));

  let formIsValid = false;
  if(enteredFirstNameIsValid && enteredLastNameIsValid && enteredEmailIsValid){
    formIsValid = true;
  }
  const formSubmissionHandler = event => {
    event.preventDefault();

    if(!enteredFirstNameIsValid && !enteredLastNameIsValid && !enteredEmailIsValid){
      return;
    }

    resetFirstNameInput();
    resetLastNameInput();
    resetEmailInput();
  };

  const firstNameInputClasses = firstNameInputHasError? 'form-control invalid' : 'form-control';
  const lasrNameInputClasses = lastNameInputHasError? 'form-control invalid' : 'form-control';
  const emailInputClasses = emailInputHasError? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={firstNameInputClasses}>
        <div className='form-control'>
          <label htmlFor='firstName'>First Name</label>
          <input type='text' id='firstName' 
           onChange={firstNameChangedHandler} 
           onBlur={firstNameBlurHandler} 
           value={enteredFirstName}
          />
        </div>
        {firstNameInputHasError && <p className="error-text">Please Enter a valid email</p>}
        <div className={lasrNameInputClasses}>
          <label htmlFor='lastName'>Last Name</label>
          <input type='text' id='lastName' 
          onChange={lastNameChangedHandler} 
          onBlur={lastNameBlurHandler} 
          value={enteredLastName}
          />
        </div>
        {lastNameInputHasError && <p className="error-text">Please Enter a valid email</p>}
      </div>
      <div className={emailInputClasses}>
        <label htmlFor='email'>E-Mail Address</label>
        <input type='email' id='email' 
         onChange={emailChangedHandler} 
         onBlur={emailBlurHandler} 
         value={enteredEmail}
        />
      </div>
      {emailInputHasError && <p className="error-text">Please Enter a valid email</p>}
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
