    {id: 'q1', author: 'Max', text: 'Learning React is fun!'},
    {id: 'q2', author: 'Mad', text: 'I guess Learning React is fun!'},
    {id: 'q3', author: 'Saddie', text: 'IDK Learning React is fun or not!'},
];
const AllQuotes = () => {
    useEffect(() => {
        sendRequest();
    },[sendRequest]);

